///////////
// COLORS
///////////

$white: #fff;
$black: #000;

$tundora: #444;
$dove-gray: #707070;

// $fountain-blue: #5dbeba;
$fountain-blue: lightblue;

$school-bus-yellow: #ffde00;

$red: #ff0000;

$primary-color: $fountain-blue;
$secondary-color: $school-bus-yellow;

$danger: $red;
//////////
// FONTS
//////////

// Roboto
$fontRoboto: "Roboto", sans-serif;
$fontPoppins: "Poppins", sans-serif;
$fontMontserrat: "Montserrat", sans-serif;

////////////////
// FONT SIZE
////////////////

$font-size-lg: 1.2rem;
$font-size-md: 1rem;
$font-size-sm: 0.8rem;
$font-size-xs: 0.6rem;

////////////////
// FONT WEIGHT
////////////////

$fontWeightThin: 300;
$fontWeightBase: 400;
$fontWeightMedium: 500;
$fontWeightSemi: 600;
$fontWeightBold: 700;

/////////////////
// Z-INDEX
/////////////////
$zindex--header: 50;
$zindex--modal: 100;

/////////////////
// SCREEN SIZES
/////////////////

$screen-xs: 480px;
$screen-sm: 768px;
$screen-md: 992px;
$screen-lg: 1200px;

