////////////////////////////////
// BOX SIZING FOR ALL ELEMENTS
////////////////////////////////

*,
::before,
::after {
  box-sizing: border-box;
}

// We're using "em" as font-size/margin/padding unit
// It is set on the body font-size and varies depending on screen sizes
html {
  font-size: 100%;
  height: 100%;
}

body {
  height: 100%;
  font-size: 0.8em;
  font-family: $fontPoppins;

  @media (min-width: $screen-xs) {
    font-size: 0.9em;
  }

  @media (min-width: $screen-sm) {
    font-size: 1em;
  }
}

#root {
  height: 100vh;
}

//////////
// RESET
//////////

div,
section,
h1,
h2,
h3,
h4,
p,
ul,
li,
a,
label,
button,
input {
  margin: 0;
  padding: 0;
  font-weight: $fontWeightBase;
  font-family: $fontPoppins;
}

a {
  text-decoration: none;
  border: none;
  outline: none;
}

li {
  list-style: none;
}

///////////
// SVG
///////////
svg {
  width: 100%;
  height: 100%;
}

///////////
// BUTTON
///////////
button,
.btn {
  outline: none;
  border: 0;
}

// CANCELS OUTLINE INPUT
input,
button,
select,
textarea {
  &:focus {
    outline: none;
  }
}

///////////
// CLASSES
///////////
.hidden {
  display: none !important;
}

.mobile-hidden {
  display: none !important;

  @include screen-sm() {
    display: block !important;
  }
}

.mobile-hidden {
  display: none !important;

  @include screen-sm() {
    display: block !important;
  }
}

.invisible {
  visibility: hidden !important;
}

//////////
// HACKS
//////////

//CANCELS SAFARI INPUT ICON
input::-webkit-contacts-auto-fill-button {
  visibility: hidden;
  display: none !important;
  pointer-events: none;
  position: absolute;
  right: 0;
}

input:focus::-webkit-contacts-auto-fill-button {
  visibility: hidden;
  display: none !important;
  pointer-events: none;
  position: absolute;
  right: 0;
}

//CANCELS INTERNET EXPLORER AND EDGE CLEAR INPUT ICON
input::-ms-clear {
  display: none;
}

//CANCELS THE YELLOW BACKGROUND COLOR ON INPUT AUTOFILL ON CHROME
input:-webkit-autofill,
input:-webkit-autofill:hover,
input:-webkit-autofill:focus,
textarea:-webkit-autofill,
textarea:-webkit-autofill:hover,
textarea:-webkit-autofill:focus,
select:-webkit-autofill,
select:-webkit-autofill:hover,
select:-webkit-autofill:focus {
  -webkit-box-shadow: 0 0 0px 1000px transparent inset;
  transition: background-color 5000s ease-in-out 0s;
}

