.header {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  width: 100%;
  z-index: $zindex--header;
  display: flex;
  padding: 1rem;
}

// TITLE
.header--title__container {
  flex: 1;
  height: 50px;
}

.header--title {
  display: flex;
  justify-content: center;
  align-items: center;
  color: $white;
  font-size: 1.3rem;
  background-color: transparent;
  cursor: pointer;

  &--firstname,
  &--lastname {
    width: 14px;
    overflow: hidden;
    display: inline-block;
    transition: width 0.4s ease-in, text-shadow 0.2s ease-out,
      color 0.2s ease-out;
  }

  &--firstname {
    width: 12px;
  }

  &--lastname {
    width: 15px;
    font-weight: bold;
  }

  &:hover {
    color: $primary-color;
    text-shadow: 0 0 4px rgba($color: $primary-color, $alpha: 1);
  }

  &.active {
    .header--title--firstname {
      width: 70px;
    }
    .header--title--lastname {
      width: 80px;
    }

    + .header--title--subtitle {
      height: 20px;
    }
  }
}

// SUBTITLE
.header--title--subtitle {
  // display: inline-block;
  color: $white;
  height: 0;
  overflow: hidden;
  transition: 0.4s ease-in;
}

// SOCIAL
.header--social__container {
  position: fixed;
  z-index: -1;

  width: 400px;
  height: 400px;
  background: rgba(255, 255, 255, 0.4);
  border-radius: 50%;
  border: 2px solid $white;
  top: -200px;
  left: -200px;
  // opacity: 0.4;
  transform: scale(0);

  transition: 0.4s ease-in;
  animation: socialBlockAnimationOut 0.6s ease-in;

  &.active {
    // box-shadow: 0 0 100000px 1000px red;
    animation: socialBlockAnimationIn 0.6s ease-in;
    transform: scale(1);
  }

  @keyframes socialBlockAnimationIn {
    0% {
      transform: scale(0);
    }
    70% {
      transform: scale(1.15);
    }
    85% {
      transform: scale(0.95);
    }
    100% {
      transform: scale(1);
    }
  }

  @keyframes socialBlockAnimationOut {
    0% {
      transform: scale(1);
    }
    30% {
      transform: scale(1.15);
    }
    100% {
      transform: scale(0);
    }
  }
  // &.inActive {
  //   // box-shadow: 0 0 100000px 1000px red;
  //   animation: socialBlockAnimationHide 0.6s ease-in;
  //   transform: scale(0);
  // }
}

.header--social--list {
  position: absolute;
  bottom: 20px;
  right: 160px;
}

.header--social--item {
  width: 24px;
  margin: 0 0 0.5rem;

  &:hover {
    svg {
      fill: #808c9f;
    }
  }
  svg {
    fill: $white;
    transition: 0.3s ease-in;
  }
}

